// Function for timestamp to readable time
function unixTimestampToHumanReadable(unixTimestamp) {
    const date = new Date(unixTimestamp);
    const year = date.getFullYear();
    const month = (date.getMonth() + 1).toString().padStart(2, '0');
    const day = date.getDate().toString().padStart(2, '0');
    const hours = date.getHours().toString().padStart(2, '0');
    const minutes = date.getMinutes().toString().padStart(2, '0');
    const seconds = date.getSeconds().toString().padStart(2, '0');

    return `${year}-${month}-${day} ${hours}:${minutes}:${seconds}`;
}

function arrayToCSV(data) {
    // Verificar si el array de datos está vacío
    if (data.length === 0) {
        return '';
    }

    // Obtener las claves del primer objeto JSON para los encabezados de columna
    const keys = Object.keys(data[0]);

    // Construir los encabezados de columna
    const headerRow = keys.join(',');

    // Construir filas del CSV
    const csvRows = [];
    csvRows.push(headerRow);

    for (const obj of data) {
        const values = keys.map(key => obj[key]);
        const csvRow = values.join(',');
        csvRows.push(csvRow);
    }

    // Unir todas las filas con saltos de línea
    return csvRows.join('\n');
}
